import React, { useState} from "react";
import { Link,Outlet } from "react-router-dom";
import '../styles/signup.css';
import axios from 'axios';

function SignUp() {
    const renderForm = (
        <div>
            <h1>Sign Up for an account</h1>

            <form id='signUpForm'>
                <label for='firstname' id='firstname'>First Name</label>
                <input type='text' name='firstname' placeholder='Jane'/>

                <label for='lastname' id='lastname'>Last Name</label>
                <input type='text' name='lastname' placeholder='Doe'/>

                <label for='username' id='username'>Email (Username)</label>
                <input type='text' name='username' placeholder='name@email.com'/>

                <label for='password' id='password'>Password</label>
                <input type='password' name='password' />

                <label for='repassword' id='repassword'>Re-enter Password</label>
                <input type='repassword' name='repassword' />

                <p>Have an account already? <a href="/">Login here!</a></p>

                <input type='submit'>Register</input>
            </form>
        </div>
    )
        return (
            <div className="app">
             <div className="signUpForm">
            <div className="title">User Registration</div>
                 {isSubmitted ? 
                    <div>User Registered successfully </div>   
                    : renderForm}
            </div>
        </div>
        )
}

export default SignUp;
