import React, { useState, useEffect } from "react";
import axios from 'axios';
import '../styles/Shop.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping } from '@fortawesome/free-solid-svg-icons';

function Cart() {

    const [cart, setCart] = useState([]);

    let currentcart;
    currentcart = JSON.parse(localStorage.getItem("currentCart"));

    currentcart.forEach(getCartData);

    const BookItem = (
        <div classname='shopInventory'>
        <div id='bookCard'>
            <img src={book.book_img} width=
            '100%' height='100%'/>
            <h3>{book.book_name}</h3>
            <h5>By {book.author_name}</h5>
            ${book.price}
            <br/>
            <b>Stock:</b> {book.stock}
            <br/>
        </div>
        </div>
    );

    function getCartData(book){
        axios.get(`http://localhost:3001/cart/${book}`).then((response) => {
            const data = response.data
            setCart(data);
        })
        .catch((error) => {
            console.log(error)
        })
    }

    return (
        <>
            <div classname='shopSection'>
            <center>
                <h1>Find out what's waiting for you today!</h1>
                <p>Puruse our wide variety of books! We are always restocking and buying what our customers want!</p>
            </center> 
            {cart.map(cart => <Cart key={book.book_name} {...cart} />)}
            </div>
            <div id='clear-both'></div>
            </>
      )
}

export default Cart;
