import React, { useState, useEffect } from "react";
import axios from 'axios';
import '../styles/Cart.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping } from '@fortawesome/free-solid-svg-icons';

function Cart() {

    const [cart, setCart] = useState([]);

    let currentcart;
    currentcart = JSON.parse(localStorage.getItem("currentCart"));

    const BookItem = ({
        book_name,
        price, 
        author_name, 
        stock,
        book_img
      }) => (
        <div classname='shopInventory'>
        <div id='bookCard'>
            <img src={book_img} width=
            '100%' height='100%'/>
            <h3>{book_name}</h3>
            <h5>By {author_name}</h5>
            ${price}
            <br/>
        </div>
        </div>
    );

    useEffect(() => {
    currentcart.map((book) =>
        axios.get(`http://localhost:3001/cart/${book}`).then((response) => {
            const data = response.data
            setCart(data)
        })
        .catch((error) => {
            console.log(error)
        })
    )}, [currentcart.length]);

    console.log(cart);

    return (
        <>
            <div classname='cartSection'>
                <h1>Cart Order</h1>
                    <div>
                        {cart.map(cart => <BookItem key={cart.book_name} {...cart} />)}
                    </div>
                <br/>
                <button className='checkoutBtn'><FontAwesomeIcon icon={faCartShopping}/> Checkout</button>
            </div>




            <div id='clear-both'></div>
        </>
      )
}

export default Cart;
